
(function() {
    // Load plugin specific language pack
    tinymce.PluginManager.requireLangPack('Block');

    tinymce.create('tinymce.plugins.Block', {

        init : function(ed, url) {
            ed.addCommand('mceBlock', function() {
                ed.windowManager.open({
                    file : url + '/blockdialog.htm',
                    width : 672 + parseInt(ed.getLang('Block.delta_width', 0)),
                    height : 612 + parseInt(ed.getLang('Block.delta_height', 0)),
                    inline : 1                    
                }, {
                    plugin_url : url, // Plugin absolute URL
                    some_custom_arg : 'custom arg', // Custom argument                    
                });
            });

            // Register example button
            ed.addButton('Block', {
                title: 'Chèn block tin liên quan',
                cmd : 'mceBlock',
                image : url + '/img/block.png'
            });

            // Add a node change handler, selects the button in the UI when a image is selected
            //ed.onNodeChange.add(function(ed, cm, n) {
            ed.on('change', function(ed, cm, n) {
                //cm.setActive('Block', n.nodeName == 'IMG');
            });
        },

        createControl : function(n, cm) {
            return null;
        },

        getInfo : function() {
            return {
                longname : 'Block Code plugin',
                author : '',
                authorurl : 'http://.vn',
                infourl: 'http://.vn',
                version : "1.0"
            };
        }
    });

    // Register plugin
    tinymce.PluginManager.add('Block', tinymce.plugins.Block);
})();
