
(function() {
    // Load plugin specific language pack
    tinymce.PluginManager.requireLangPack('Pastez');

    tinymce.create('tinymce.plugins.Pastez', {

        init : function(ed, url) {
            ed.addCommand('mcePastez', function() {
                ed.windowManager.open({
                    file : url + '/Pastezdialog.htm',
                    width : 672 + parseInt(ed.getLang('Pastez.delta_width', 0)),
                    height : 480 + parseInt(ed.getLang('Pastez.delta_height', 0)),
                    inline : 1                    
                }, {
                    plugin_url : url, // Plugin absolute URL
                    some_custom_arg : 'custom arg', // Custom argument                    
                });
            });

            // Register example button
            ed.addButton('Pastez', {
                title: 'Paste from Notepad',
                cmd : 'mcePastez',
                image : url + '/img/Pastez.png'
            });

            // Add a node change handler, selects the button in the UI when a image is selected
            ed.on('change', function(ed, cm, n) {
                cm.setActive('Pastez', n.nodeName == 'IMG');
            });
        },

        createControl : function(n, cm) {
            return null;
        },

        getInfo : function() {
            return {
                longname : 'Paste from Notepad',
                author : '',
                authorurl : 'http://.vn',
                infourl: 'http://.vn',
                version : "1.0"
            };
        }
    });

    // Register plugin
    tinymce.PluginManager.add('Pastez', tinymce.plugins.Pastez);
})();
