
(function() {
    // Load plugin specific language pack
    tinymce.PluginManager.requireLangPack('attach');

    tinymce.create('tinymce.plugins.attach', {

        init : function(ed, url) {
            ed.addCommand('mceAttach', function() {
                ed.windowManager.open({
                    file : url + '/dialog.htm',
                    width : 672 + parseInt(ed.getLang('Attach.delta_width', 0)),
                    height : 436 + parseInt(ed.getLang('Attach.delta_height', 0)),
                    inline : 1                    
                }, {
                    plugin_url : url, // Plugin absolute URL
                    some_custom_arg : 'custom arg', // Custom argument                    
                });
            });

            // Register example button
            ed.addButton('attach', {
                title: 'Ch√®n audio',
                cmd : 'mceAttach',
                image : url + '/img/attach.png'
            });

            // Add a node change handler, selects the button in the UI when a image is selected
            ed.on('change', function(ed, cm, n) {
                //cm.setActive('video', n.nodeName == 'IMG');
            });
        },

        createControl : function(n, cm) {
            return null;
        },

        getInfo : function() {
            return {
                longname : 'Attach Insert plugin',
                author : '',
                authorurl : 'http://.vn',
                infourl: 'http://.vn',
                version : "1.0"
            };
        }
    });

    // Register plugin
    tinymce.PluginManager.add('attach', tinymce.plugins.attach);
})();
