
(function() {
    // Load plugin specific language pack
    tinymce.PluginManager.requireLangPack('images');

    tinymce.create('tinymce.plugins.images', {

        init : function(ed, url) {
            ed.addCommand('mceImage', function() {
                ed.windowManager.open({
                    file : url + '/dialog.php',
                    width : 672 + parseInt(ed.getLang('Images.delta_width', 0)),
                    height : 436 + parseInt(ed.getLang('Images.delta_height', 0)),
                    inline : 1                    
                }, {
                    plugin_url : url, // Plugin absolute URL
                    some_custom_arg : 'custom arg', // Custom argument                    
                });
            });

            // Register example button
            ed.addButton('images', {
                title: 'Chèn hình ảnh',
                cmd : 'mceImage',
                image : url + '/img/Picture.png'
            });

            // Add a node change handler, selects the button in the UI when a image is selected
            //ed.onNodeChange.add(function(ed, cm, n) {
            ed.on('change', function(ed, cm, n) {
                //cm.setActive('images', n.nodeName == 'IMG');
            });
        },

        createControl : function(n, cm) {
            return null;
        },

        getInfo : function() {
            return {
                longname : 'Images Insert plugin',
                author : '',
                authorurl : 'http://.vn',
                infourl: 'http://.vn',
                version : "1.0"
            };
        }
    });

    // Register plugin
    tinymce.PluginManager.add('images', tinymce.plugins.images);
})();
