
(function() {
    // Load plugin specific language pack
    tinymce.PluginManager.requireLangPack('quotez');

    tinymce.create('tinymce.plugins.quotez', {

        init : function(ed, url) {
            ed.addCommand('mceQuotez', function() {
                ed.windowManager.open({
                    file : url + '/dialog.htm',
                    width : 500 + parseInt(ed.getLang('quotez.delta_width', 0)),
                    height : 320 + parseInt(ed.getLang('quotez.delta_height', 0)),
                    inline : 1                    
                }, {
                    plugin_url : url, // Plugin absolute URL
                    some_custom_arg : 'custom arg', // Custom argument                    
                });
            });

            // Register example button
            ed.addButton('quotez', {
                title: 'Chèn trích dẫn',
                cmd : 'mceQuotez',
                image : url + '/asset/images/quote.png'
            });

            // Add a node change handler, selects the button in the UI when a image is selected
            ed.on('change', function(ed, cm, n) {
                //cm.setActive('Block', n.nodeName == 'IMG');
            });
        },

        createControl : function(n, cm) {
            return null;
        },

        getInfo : function() {
            return {
                longname : 'Quotez Code plugin',
                author : '',
                authorurl : 'http://.vn',
                infourl: 'http://.vn',
                version : "1.0"
            };
        }
    });

    // Register plugin
    tinymce.PluginManager.add('quotez', tinymce.plugins.quotez);
})();
