
(function() {
    // Load plugin specific language pack
    tinymce.PluginManager.requireLangPack('slide');

    tinymce.create('tinymce.plugins.slide', {

        init : function(ed, url) {
            ed.addCommand('mceSlide', function() {
                ed.windowManager.open({
                    file : url + '/dialog.htm',
                    width : 672 + parseInt(ed.getLang('Images.delta_width', 0)),
                    height : 436 + parseInt(ed.getLang('Images.delta_height', 0)),
                    inline : 1                    
                }, {
                    plugin_url : url, // Plugin absolute URL
                    some_custom_arg : 'custom arg', // Custom argument                    
                });
            });

            // Register example button
            ed.addButton('slide', {
                title: 'Chèn slide ảnh',
                cmd : 'mceSlide',
                image : url + '/img/slide.png'
            });

            // Add a node change handler, selects the button in the UI when a image is selected
            ed.on('change', function(ed, cm, n) {
                cm.setActive('slide', ($(n).attr('class') == 'tkp_tinyslide' || $(n).attr('class') == 'tkp_one'));
            });
        },

        createControl : function(n, cm) {
            return null;
        },

        getInfo : function() {
            return {
                longname : 'Slide Insert plugin',
                author : '',
                authorurl : 'http://.vn',
                infourl: 'http://.vn',
                version : "1.0"
            };
        }
    });

    // Register plugin
    tinymce.PluginManager.add('slide', tinymce.plugins.slide);
})();
