
(function() {
    // Load plugin specific language pack
    tinymce.PluginManager.requireLangPack('video');

    tinymce.create('tinymce.plugins.video', {

        init : function(ed, url) {
            ed.addCommand('mceVideo', function() {
                ed.windowManager.open({
                    file : url + '/dialog.php',
                    width : 672 + parseInt(ed.getLang('Video.delta_width', 0)),
                    height : 436 + parseInt(ed.getLang('Video.delta_height', 0)),
                    inline : 1                    
                }, {
                    plugin_url : url, // Plugin absolute URL
                    some_custom_arg : 'custom arg', // Custom argument                    
                });
            });

            // Register example button
            ed.addButton('video', {
                title: 'Ch√®n video',
                cmd : 'mceVideo',
                image : url + '/img/video.png'
            });

            // Add a node change handler, selects the button in the UI when a image is selected
            ed.on('change', function(ed, cm, n) {
                //cm.setActive('video', n.nodeName == 'IMG');
            });
        },

        createControl : function(n, cm) {
            return null;
        },

        getInfo : function() {
            return {
                longname : 'Video Insert plugin',
                author : 'Tekplus',
                authorurl : 'http://tekplus.com.vn',
                infourl: 'http://tekplus.com.vn',
                version : "1.0"
            };
        }
    });

    // Register plugin
    tinymce.PluginManager.add('video', tinymce.plugins.video);
})();
