
(function() {
    // Load plugin specific language pack
    tinymce.PluginManager.requireLangPack('vote');

    tinymce.create('tinymce.plugins.vote', {

        init : function(ed, url) {
            ed.addCommand('mceVote', function() {
                ed.windowManager.open({
                    file : url + '/dialog.htm',
                    width : 300 + parseInt(ed.getLang('Vote.delta_width', 0)),
                    height : 436 + parseInt(ed.getLang('Vote.delta_height', 0)),
                    inline : 1                    
                }, {
                    plugin_url : url, // Plugin absolute URL
                    some_custom_arg : 'custom arg', // Custom argument                    
                });
            });

            // Register example button
            ed.addButton('vote', {
                title: 'Thăm dò ý kiến',
                cmd : 'mceVote',
                image : url + '/img/vote.png'
            });

            // Add a node change handler, selects the button in the UI when a image is selected
            ed.on('change', function(ed, cm, n) {
                cm.setActive('vote', (n.nodeName == 'DIV' && $(n).attr('class') == 'tkp_area_vote'));
            });
        },

        createControl : function(n, cm) {
            return null;
        },

        getInfo : function() {
            return {
                longname : 'Vote Insert plugin',
                author : '',
                authorurl : 'http://.vn',
                infourl: 'http://.vn',
                version : "1.0"
            };
        }
    });

    // Register plugin
    tinymce.PluginManager.add('vote', tinymce.plugins.vote);
})();
