
(function() {
    // Load plugin specific language pack
    tinymce.PluginManager.requireLangPack('votemusic');

    tinymce.create('tinymce.plugins.votemusic', {

        init : function(ed, url) {
            ed.addCommand('mceVotemusic', function() {
                ed.windowManager.open({
                    file : url + '/dialog.htm',
                    width : 550 + parseInt(ed.getLang('Votemusic.delta_width', 0)),
                    height : 436 + parseInt(ed.getLang('Votemusic.delta_height', 0)),
                    inline : 1                    
                }, {
                    plugin_url : url, // Plugin absolute URL
                    some_custom_arg : 'custom arg', // Custom argument                    
                });
            });

            // Register example button
            ed.addButton('votemusic', {
                title: 'Thăm dò ý kiến âm nhạc',
                cmd : 'mceVotemusic',
                image : url + '/img/vote.png'
            });

            // Add a node change handler, selects the button in the UI when a image is selected
            ed.on('change', function(ed, cm, n) {
                cm.setActive('votemusic', (n.nodeName == 'DIV' && $(n).attr('class') == 'tkp_area_votemusic'));
            });
        },

        createControl : function(n, cm) {
            return null;
        },

        getInfo : function() {
            return {
                longname : 'Vote Insert plugin',
                author : '',
                authorurl : 'http://.vn',
                infourl: 'http://.vn',
                version : "1.0"
            };
        }
    });

    // Register plugin
    tinymce.PluginManager.add('votemusic', tinymce.plugins.votemusic);
})();
